mapper.update:
UPDATE account
SET
name=#{name}, role_id=#{roleId},
enabled=#{enabled},
updated_at=#{updatedAt}
WHERE id=#{id}

UPDATE account
SET
enabled=1,
updated_at=#{updatedAt}
WHERE id=#{id}

UPDATE account
SET
enabled=0,
updated_at=#{updatedAt}
WHERE id=#{id}

UPDATE account
SET
password=#{password},
updated_at=#{updatedAt}
WHERE id=#{id}
