mapper.update:
UPDATE production
SET
name=#{name}, description=#{description}, enabled=#{enabled},
dir=#{dir}, updated_at=#{updatedAt}
WHERE id=#{id}

UPDATE production
SET enabled=true,
updated_at=#{updatedAt}
WHERE id=#{id}

UPDATE production
SET enabled=false,
updated_at=#{updatedAt}
WHERE id=#{id}
